import React, { useState } from "react";

const MarkdownInput = ({updateTitle, updateContent}) =>{
  const [inputValue, setInputValue] = useState("");
  const [textareaValue, setTextareaValue] = useState("");

  const handleTitleChange = (e)=>{
    setInputValue(e.target.value);
    updateTitle(e.target.value);
  }

  const handleContentChange = (e)=>{
    setTextareaValue(e.target.value);
    updateContent(e.target.value);
  }

  const handleClickSave = (e)=>{
    let saves = localStorage.getItem("saves");

    if (saves){

    } else {
      localStorage.setItem("saves", JSON.stringify([{title: inputValue, content: textareaValue}]));
    }
  }

  return (
    <>
      <div className="mdown">
        <input type = "text" onChange={handleTitleChange}></input>
        <p><textarea id = "textarea_conten" type = "text" onChange={handleContentChange}></textarea></p>
        <p><button id = "save_button" onClick={handleClickSave}>Sauvegarder</button></p>
      </div>
    </>
  )
}

export default MarkdownInput;
