      if (content[i]=='\n'){
        lines.push(currentLine);
        currentLine = '';
      } else {
        currentLine+= content[i];
      }
    }
  }

  return (
    <>
      <h2>{title}</h2>
      <pre>{content}</pre>
    </>
  )
}

export default NoteDisplay;
